fn main() {
    let input = "";
    let numbers : Vec<i32> = input.chars().map(|n| n.to_digit(10).unwrap() as i32).collect();
    let last_index = (numbers.len() - 1) as i32;
    let half_len = (numbers.len()/2) as i32;


    get_valid_sum(&numbers, 1, last_index);
    get_valid_sum(&numbers, half_len, last_index);
}

fn get_valid_sum(numbers: &Vec<i32>, space: i32, last_index: i32){
    let mut to_sum:Vec<i32> = Vec::new();

    for start_index in 0..last_index+1{
        let (a,b) = get_indices_to_compare(start_index, space, last_index);
        to_sum = check(numbers[a as usize],numbers[b as usize], &mut to_sum);
    }

    let final_sum : i32 = to_sum.iter().sum();

    println!("Sum of valid numbers: {}", final_sum);

}

fn get_indices_to_compare(start_index: i32, space: i32, last_index: i32) -> (i32,i32){
    if start_index+space <= last_index {(start_index,start_index+space)}
    else {(start_index, (start_index+space) - (last_index+1))}

}

fn check(a:i32, b:i32, to_sum: &mut Vec<i32>) -> Vec<i32>{
    if a==b{
        to_sum.push(a);
        to_sum.to_owned()
    }
        else { to_sum.to_owned() }
}
